/*
const employees = [[1, 'David', 'Lenovo'], 
[2, 'Brian', 'Android'], 
[3, 'David', 'Lenovo']];
*/
const retrieveDetails = function(employees, basedOn, property) {
  const target = [];

  for(let index = 0; index < employees.length; index++) {
    const employee = employees[index].concat();
    if(basedOn(employee, property)) {
      target.push(employee);
    }
  }

  return target;
};

const basedOnId = function(employee, id) {
  return employee[0] === id;
};

const basedOnName = function(employee, empName) {
  return employee[1] === empName;
};

const basedOnProject = function(employee, project) {
  return employee[2] === project;
};

const sameNameAndProject = function(employees, empName, project) {
  const empsInProject = retrieveDetails(employees, basedOnProject, project);

  return retrieveDetails(empsInProject, basedOnName, empName);
};

const isEqual1d = function(array1, array2) {
  let flag = true;

  for(let col = 0; col < array1.length || col < array2.length; col++) {
    if(array1[col] !== array2[col]) {
      flag = false;
    }
  }

  return flag;
};

const isEqual2d = function(array1, array2) {
  let flag = true;

  for(let row = 0; row < array1.length || row < array2.length; row++) {
    flag = isEqual1d(array1[row], array2[row]);
  }

  return flag;
};

const factorial = function(num) {
  let result = 1;

  if(num < 0) {
    return;
  }

  for(let count = num; count > 1; count--) {
    result *= count;
  }
  return result;
};

const nthFibonacciNumber = function(num) {
  let first = 0;
  let second = 1;
  let third;

  if(num <= 1) {
    return 0;
  }

  for(let count = 0; count < num - 2; count++) {
    third = first + second;
    first = second;
    second = third;
  }

  return second;
};

const fibonacciSeries = function(num) {
  const arr = [];
  for(let count = 1; count <= num; count++) {
    arr.push(nthFibonacciNumber(count));
  }
  return arr;
};

const filterEvensOdds = function(numbers, filter) {
  const filteredNumbers = [];

  for(let index = 0; index < numbers.length; index++) {
    if(filter(numbers[index])) {
      filteredNumbers.push(numbers[index]);
    }
  }

  return filteredNumbers;
};

