const removeAdjacentDuplicates = function(numbers) {
  const result = [];

  for(let index = 0; index < numbers.length; index++) {
    if(numbers[index] !== numbers[index + 1]) {
      result.push(numbers[index]);
    }
  }

  return result;
};

