const isFromClass8 = function(student) {
  return student[1] === 8;
};

const isFromClass9 = function(student) {
  return student[1] === 9;
};

const isFromClass7 = function(class7, student) {
  if(student[1] === 7) {
    class7.push(student);
  }

  return class7;
};

// Find the Students of class 7 using reduce
const studentsOfClass7 = function(students) {
  return students.reduce(isFromClass7, []);
};

const studentsOfClass8 = function(students) {
  return students.filter(isFromClass8);
};

const studentsOfClass9 = function(students) {
  return students.filter(isFromClass9);
};

const sumOfMarks = function(sum, student) {
  return student[2] + sum;
};

// Find the Average marks of class 9 using filter and reduce
const averageOfClass9 = function(students) {
  const class9Students = studentsOfClass9(students);
  const totalMarks = class9Students.reduce(sumOfMarks, 0);
  return totalMarks / class9Students.length;
};

const class8sum = function(sum, student) {
  if(student[1] !== 8) {
    return sum;
  }

  sum[0]++;
  sum[1] += student[2];
  return sum;
};

//Find the Average marks of class 8 using only one reduce
const averageOfClass8 = function(students) {
  const count = students.reduce(class8sum, [0, 0]);
  return count[1] / count[0];
};

const addNumbers = function(array) {
  let sum = 0;

  for(let index = 0; index < array.length; index++) {
    sum += array[index];
  }
  return sum;
};
